BEGIN TRANSACTION;
CREATE TABLE booking_spider (
	city TEXT, 
	name TEXT, 
	note TEXT, 
	url TEXT, 
	description TEXT, 
	depth BIGINT, 
	download_timeout BIGINT, 
	download_slot TEXT, 
	download_latency FLOAT, 
	gps_hotel TEXT
);
CREATE TABLE hotel_infos (
	"Unnamed: 0" BIGINT, 
	city TEXT, 
	name TEXT, 
	note TEXT, 
	url TEXT, 
	description TEXT, 
	coord_lat FLOAT, 
	coord_lon FLOAT
);
CREATE TABLE top_5city (
	"Unnamed: 0" BIGINT, 
	"index" BIGINT, 
	name TEXT, 
	latitude FLOAT, 
	longitude FLOAT, 
	"Humidity" BIGINT, 
	"Temperature_avg" FLOAT, 
	"Temperature_min" FLOAT, 
	"Temperature_max" FLOAT, 
	"Temperature_perceived" FLOAT, 
	"Wind_speed" FLOAT, 
	"Cloudiness" BIGINT, 
	"Precipitation_prob" BIGINT, 
	"Cloudiness_adjusted" FLOAT, 
	"Precipitation_prob_adjusted" FLOAT
);
COMMIT;
